// Custom JS
"use strict";
var n = jQuery.noConflict();
jQuery(window).on('load', function () {
    jQuery(".pageloader").fadeOut("slow");
});


function FooterToggleBar() {
    if (window.innerWidth < 1025) {
        jQuery('footer .widget .wp-block-heading').parent().removeClass('toggled-on').addClass('toggled-off');
        jQuery('footer .widget .wp-block-heading').off("click").on("click", toggleFunction);
    } else {
        jQuery('footer .widget .wp-block-heading').parent().removeClass('toggled-off').addClass('toggled-on');
        jQuery('footer .widget .wp-block-heading').off("click");
    }
}


jQuery(document).ready(function () {
    "use strict";
    var css = '<style>\
    footer .widget .wp-block-heading {\
        transition: all 0.3s ease; /* Adjust the duration and timing function as needed */\
    }\
    footer .widget .toggled-off ul{\
        display: none; /* Example style for toggled-off state */\
    }\
    footer .widget .wp-block-heading.toggled-on {\
        display: block; /* Example style for toggled-on state */\
    }\
    </style>';
    jQuery('head').append(css);
    FooterToggleBar()
});

function toggleFunction() {
    jQuery(this).parent().toggleClass('toggled-on toggled-off');
}

window.addEventListener("resize", function () {
    if (window.innerWidth < 1025) {
        jQuery('footer .widget .wp-block-heading').parent().removeClass('toggled-on').addClass('toggled-off');
        jQuery('footer .widget .wp-block-heading').off("click").on("click", toggleFunction);
    } else {
        jQuery('footer .widget .wp-block-heading').parent().removeClass('toggled-off').addClass('toggled-on');
        jQuery('footer .widget .wp-block-heading').off("click");
    }

    FooterToggleBar();
});



